import Vue from 'vue'
import VueRouter from 'vue-router'
import Layout from '@/layouts/BasicLayout'

// 解决重复导航到同一路由的错误
const originalPush = VueRouter.prototype.push
VueRouter.prototype.push = function push(location) {
  return originalPush.call(this, location).catch(err => err)
}

Vue.use(VueRouter)

// 路由配置
export const routes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/login/index'),
    meta: { title: '登录', hidden: true }
  },
  {
    path: '/404',
    component: () => import('@/views/error/404'),
    meta: { hidden: true }
  },
  {
    path: '/',
    component: Layout,
    redirect: '/dashboard',
    children: [
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: () => import('@/views/dashboard/index'),
        meta: { title: '首页', icon: 'dashboard' }
      }
    ]
  },
  {
    path: '/order',
    component: Layout,
    name: 'OrderManage',
    meta: { title: '订单管理', icon: 'profile' },
    children: [
      {
        path: 'list',
        name: 'OrderList',
        component: () => import('@/views/order/index'),
        meta: { title: '订单列表' }
      },
      {
        path: 'create',
        name: 'OrderCreate',
        component: () => import('@/views/order/create'),
        meta: { title: '录入订单' }
      },
      {
        path: 'detail/:id',
        name: 'OrderDetail',
        component: () => import('@/views/order/detail'),
        meta: { title: '订单详情', hidden: true }
      },
      {
        path: 'statistics',
        name: 'OrderStatistics',
        component: () => import('@/views/order/statistics'),
        meta: { title: '订单统计' }
      }
    ]
  },
  {
    path: '/member',
    component: Layout,
    name: 'MemberManage',
    meta: { title: '会员管理', icon: 'user' },
    children: [
      {
        path: 'list',
        name: 'MemberList',
        component: () => import('@/views/member/index'),
        meta: { title: '会员列表' }
      },
      {
        path: 'create',
        name: 'MemberCreate',
        component: () => import('@/views/member/create'),
        meta: { title: '添加会员' }
      },
      {
        path: 'detail/:id',
        name: 'MemberDetail',
        component: () => import('@/views/member/detail'),
        meta: { title: '会员详情', hidden: true }
      }
    ]
  },
  {
    path: '/clothes',
    component: Layout,
    name: 'ClothesManage',
    meta: { title: '衣物管理', icon: 'shopping' },
    children: [
      {
        path: 'category',
        name: 'CategoryList',
        component: () => import('@/views/clothes/category'),
        meta: { title: '衣物类别' }
      },
      {
        path: 'price',
        name: 'PriceList',
        component: () => import('@/views/clothes/price'),
        meta: { title: '价格设置' }
      }
    ]
  },
  {
    path: '/finance',
    component: Layout,
    name: 'FinanceManage',
    meta: { title: '财务管理', icon: 'money-collect' },
    children: [
      {
        path: 'summary',
        name: 'FinanceSummary',
        component: () => import('@/views/finance/summary'),
        meta: { title: '财务总览' }
      },
      {
        path: 'statement',
        name: 'FinanceStatement',
        component: () => import('@/views/finance/statement'),
        meta: { title: '财务报表' }
      }
    ]
  },
  {
    path: '/system',
    component: Layout,
    name: 'SystemManage',
    meta: { title: '系统设置', icon: 'setting' },
    children: [
      {
        path: 'user',
        name: 'UserManage',
        component: () => import('@/views/system/user'),
        meta: { title: '用户管理' }
      },
      {
        path: 'role',
        name: 'RoleManage',
        component: () => import('@/views/system/role'),
        meta: { title: '角色管理' }
      },
      {
        path: 'store',
        name: 'StoreManage',
        component: () => import('@/views/system/store'),
        meta: { title: '门店设置' }
      },
      {
        path: 'setting',
        name: 'SystemSetting',
        component: () => import('@/views/system/setting'),
        meta: { title: '系统参数' }
      }
    ]
  },
  {
    path: '/profile',
    component: Layout,
    name: 'Profile',
    meta: { hidden: true },
    children: [
      {
        path: 'index',
        name: 'UserProfile',
        component: () => import('@/views/profile/index'),
        meta: { title: '个人中心' }
      },
      {
        path: 'password',
        name: 'ChangePassword',
        component: () => import('@/views/profile/password'),
        meta: { title: '修改密码' }
      }
    ]
  },
  // 404 页面必须放在最后
  { path: '*', redirect: '/404', meta: { hidden: true } }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
